mapper.select:

select 

from PermitExtend
where PermitNo = #{permitNo,jdbcType=VARCHAR}
  and SerialNo = #{serialNo,jdbcType=NUMERIC}

mapper.delete:

delete from PermitExtend
where PermitNo = #{permitNo,jdbcType=VARCHAR}
  and SerialNo = #{serialNo,jdbcType=NUMERIC}

mapper.select:
    SELECT min(SerialNo) SerialNo
	FROM PermitExtend
	WHERE PermitNo = #{permitNo,jdbcType=VARCHAR}
